import axios from 'axios';
import * as cheerio from 'cheerio';
import * as fs from 'fs';
import * as path from 'path';
import * as url from 'url';

/**
 * Download content and save it to a file based on the URL.
 */
const downloadContent = async (pageUrl: string, content: string) => {
    const parsedUrl = url.parse(pageUrl);
    const filePath = path.join('./downloads', `${parsedUrl.hostname}${parsedUrl?.pathname?.replace(/\//g, '_')}.html`);

    // Create directory if it doesn't exist
    const dir = path.dirname(filePath);
    if (!fs.existsSync(dir)) {
        fs.mkdirSync(dir, { recursive: true });
    }

    // Write the content to the file
    fs.writeFileSync(filePath, content);
    console.log(`Downloaded content for: ${pageUrl}`);
};

/**
 * Extract all links within the same domain from the HTML content.
 */
const extractLinks = (html: string, baseUrl: string): string[] => {
    const $ = cheerio.load(html);
    const links: string[] = [];

    $('a').each((_, element) => {
        const href = $(element).attr('href');
        if (href) {
            const absoluteLink = url.resolve(baseUrl, href);
            if (absoluteLink.startsWith(baseUrl)) {
                links.push(absoluteLink);
            }
        }
    });

    return links;
};

/**
 * Fetch the page content using axios and return it.
 */
const fetchPageContent = async (pageUrl: string): Promise<string> => {
    try {
        const response = await axios.get(pageUrl);
        return response.data;
    } catch (error) {
        console.error(`Error fetching page: ${pageUrl}`);
        return '';
    }
};

/**
 * Main function to handle the crawling process up to one level.
 */
const crawlOneLevel = async (inputHtml: string, baseUrl: string) => {
    // Download and save the input page
    await downloadContent(baseUrl, inputHtml);

    // Extract links from the input page
    const links = extractLinks(inputHtml, baseUrl);

    // Fetch and save content for each linked page
    for (const link of links) {
        console.log(`Fetching linked page: ${link}`);
        const linkedPageContent = await fetchPageContent(link);
        if (linkedPageContent) {
            await downloadContent(link, linkedPageContent);
        }
    }
};

